import "./style.css";

const Buttons = ({ tasks, hideDoneTasks }) => {
    if (tasks.lenght === 0) {
        return null;
    }

    return (
        <div className="buttons">
            {tasks.length > 0 && (
            <>
                <button className=" buttons__button">
                {hideDoneTasks ? "Pokaż" : "Ukryj"} ukończone
            </button>
            <button 
            className="buttons__button"
            disabled={tasks.every(({ done }) => done)}
            >
            Ukończ wszystkie
            </button>
            </>
            )}
        </div>
    )
};

export default Buttons
